import React, { PureComponent, ReactElement } from 'react'

export default function View(props: IView) {
  const { c, s, children, ...rest } = props

  const style = s.reduce((a: any, b: any) => {
    return { ...a, ...b }
  }, {})

  return (
    <div className={c} style={style} {...rest}>
      {children}
    </div>
  )
}

export interface IView {
  c?: string // class
  s?: any // style
  children: any

  [key: string]: any
}
