import React, { PureComponent, ReactElement } from 'react'

export default function View(props: IView) {
  const { c, s, children, ...rest } = props
  const style = s ? s.reduce((a: any, b: any) => {
    return { ...a, ...b }
  }, {}) : {}

  return (
    <span className={`${c || ''}`} style={style} {...rest}>
      {children}
    </span>
  )
}

export interface IView {
  c?: string // class
  s?: any // style
  children: any

  [key: string]: any
}
