import { textColor, paletteColor } from '../theme/light'
import { o2a } from '../utils'

export default (themeColor = paletteColor) => {
  // 主题背景
  const pc = o2a(themeColor)
    .map(({ key, value }) => {
      return `.${key.replace(/\b\w/g, (l: string) =>
        l.toUpperCase(),
      )}{background-color:${value}}`
    })
    .join('')

  return pc
}
