import { textColor, paletteColor } from '../theme/light'
import { o2a } from '../utils'

export default (colors = textColor, themeColor = paletteColor) => {
  // 字体颜色
  const fc = o2a(colors)
    .map(({ key, value }) => {
      return `.f${key}{color:${value}}`
    })
    .join('')

  // 主题颜色
  const tc = o2a(themeColor)
    .map(({ key, value }) => {
      return `.${key}{color:${value}}`
    })
    .join('')

  return `${fc}${tc}`
}
