import space from '../theme/light/spacer'

const breakpoints = {
  sm: 576,
  md: 768,
  lg: 992,
  xl: 1200,
}

const contentWidth = {
  sm: 540,
  md: 720,
  lg: 960,
  xl: 1140,
}

export default (bps = breakpoints, ctw = contentWidth, spacer = space) => {
  const container = Object.keys(bps)
    .map(
      (bp: any) =>
        `@media(min-width:${(bps as any)[bp]}px){.ctn{width:${(ctw as any)[
          bp
          ]}px;padding-left:${spacer * 3}px;padding-right:${spacer * 3}px}}`,
    )
    .join('')

  return `.ctn{width:100%;margin-left:auto;margin-right:auto}
    ${container}`
}
