import spacer from '../theme/light/spacer'

export default (unit: number = spacer) => {
  return `
[class^=m_], .gh_, .gv_, .ghv_{display:flex;flex-wrap:wrap;flex-direction:row}
${[2, 3, 4, 5, 6, 7, 8, 9]
    .map((s: number) => `.m_${s} > *{width:${100 / s}%}`)
    .join('')}
${[1, 2, 3, 4]
    .map((key: number) => {
      return `
      .gh_${key}{margin-left:-${unit *
        key}px}.gh_${key} > *{padding-left:${unit * key}px}
      .gv_${key}{margin-top:-${unit * key}px}.gv_${key} > *{padding-top:${unit *
        key}px}
      .ghv_${key}{margin-left:-${unit * key}px;margin-top:-${unit * key}px}
      .ghv_${key} > *{padding-left:${unit * key}px;padding-top:${unit * key}px}
      `
    })
    .join('')}
`
}
