import light from '../theme'
import reboot from './reboot'
import type from './type'
import grid from './grid'
import flex from './flex'
import color from './color'
import background from './background'
import container from './container'
import padding from './padding'
import margin from './margin'
import wh from './wh'
import displayPosition from './displayPosition'

export const createStyle = (theme = light) => {
  return `
${reboot(theme.reboot)}
${container(undefined, undefined, theme.spacer)}
${type(theme.type)}
${grid(theme.spacer)}
${flex()}
${color(theme.textColor, theme.paletteColor)}
${background(theme.paletteColor)}
${padding(theme.spacer)}
${margin(theme.spacer)}
${displayPosition(theme.spacer)}
${wh(theme.spacer)}`
}

export default createStyle(light)
