import spacer from '../theme/light/spacer'

export default (unit: number = spacer) => {
  const sizeKeys = [0.2, 0.4, 0.6, 0.8, 1, 2, 3, 4, 5, 6, 7, 6, 9]

  const pd = sizeKeys
    .map((sizeKey: any) => {
      return `.pd${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding:${sizeKey * unit}px}`
    })
    .join('')

  const pt = sizeKeys
    .map((sizeKey: any) => {
      return `.pt${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-top:${sizeKey * unit}px}`
    })
    .join('')

  const pr = sizeKeys
    .map((sizeKey: any) => {
      return `.pr${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-right:${sizeKey * unit}px}`
    })
    .join('')

  const pb = sizeKeys
    .map((sizeKey: any) => {
      return `.pb${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-bottom:${sizeKey * unit}px}`
    })
    .join('')

  const pl = sizeKeys
    .map((sizeKey: any) => {
      return `.pl${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-left:${sizeKey * unit}px}`
    })
    .join('')

  const plr = sizeKeys
    .map((sizeKey: any) => {
      return `.plr${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-left:${sizeKey * unit}px;padding-right:${sizeKey *
        unit}px}`
    })
    .join('')

  const ptb = sizeKeys
    .map((sizeKey: any) => {
      return `.ptb${sizeKey < 1
        ? `0${sizeKey * 10}`
        : sizeKey}{padding-top:${sizeKey * unit}px;padding-bottom:${sizeKey *
        unit}px}`
    })
    .join('')

  return `${pd}${pt}${pr}${pb}${pl}${plr}${ptb}`
}
