import reboot from '../theme/light/reboot'

export default (theme = reboot) => {
  const { fontSize, lineHeight, fontColor, background } = theme
  const { linkColor, linkDecoration } = theme
  const { linkHoverColor, linkHoverDecoration } = theme

  const fs = `font-size:${fontSize}px;line-height:${lineHeight}`
  const fc = `color:${fontColor};background-color:${background}`
  const a = `a{color:${linkColor};text-decoration:${linkDecoration}}`
  const aH = `a:hover{color:${linkHoverColor};text-decoration:${linkHoverDecoration}}`

  return `body{${fs};${fc}}${a}${aH}`
}
