import type from '../theme/light/type'

export default (theme = type) => {
  const {
    headerFontColor,
    headerFontFamily,
    headerLineHeight,
    headerMarginBottom,
    headerFontWeight,
  } = theme

  const font = `
.fxxs{font-size:0.625rem}.fxs{font-size:0.75rem}.fsm{font-size:0.875rem}.fmd,h6,.h6{font-size:1rem}
.flg,h5,.h5{font-size:1.25rem}.fxl,h4,.h4{font-size:1.5rem}.fxxl,h3,.h3{font-size:1.75rem}
.fml,h2,.h2{font-size:2rem}.fmml,h1,.h1{font-size:2.5rem}
h1,h2,h3,h4,h5,h6,.h1,.h2,.h3,.h4,.h5,.h6
{margin-bottom: ${headerMarginBottom}px;
font-family: ${headerFontFamily};
font-weight:${headerFontWeight};
line-height:${headerLineHeight};
color:${headerFontColor};
}
  `

  return font
}
