interface Theme {
  container: {
    breakpoints: {
      sm: number,
      md: number,
      lg: number,
      xl: number,
    },
    contentWidth: {
      sm: number,
      md: number,
      lg: number,
      xl: number,
    },
    contentPadding: number,
  },

  content: {
    color: string,
    background: string,
    fontSize: number,
    lineHigh: number,
    fontFamily: string,

    linkColor: string,
    linkColorHover: string,
    linkDecoration: string,
    linkDecorationHover: string,
  },

  margin: {
    unit: number,
  },

  padding: {
    unit: number,
  },

  text: {
    fontSizeUnit: number,

    lineHighUnit: number,
    lineHeightUnit: number,

    fontScale: number,

    fontSizeMapping: { [key: string]: number }

    families: {
      sans: string,
      mono: string,
      quot: string,
    },

    colors: {
      primary: string,
      p7y: string,
      secondary: string,
      s9y: string,
      disabled: string,
      hint: string,
      icon: string,
      divider: string,
      lightDivider: string,
    },
  },

  themeColor: {
    primary: string,
    p7y: string,
    primaryLight: string,
    primaryDark: string,

    secondary: string,
    s9y: string,
    secondaryDark: string,
    secondaryLight: string,

    info: string,
    warning: string,
    danger: string,
    success: string,

    light: string,
    dark: string,
  },

  colorTheme: {
    primary: string,
    p7y: string,
    primaryLight: string,
    primaryDark: string,

    secondary: string,
    s9y: string,
    secondaryDark: string,
    secondaryLight: string,

    info: string,
    warning: string,
    danger: string,
    success: string,

    light: string,
    dark: string,
  },

  [key: string]: any
}
